import {
    Button,
    Card,
    Form,
    Input, List, Message,
    PageHeader,
    Select,
    Steps,
    Tag, Upload,
} from "@arco-design/web-react";
import {useEffect, useState} from "react";
import {IconLeft, IconRight} from "@arco-design/web-react/icon";
import {getIdeaDomain, postIdeaGeneratorStep1, postIdeaGeneratorStep2} from "../../../service/IdeaGenerator";
import _ from "lodash";

const TextArea = Input.TextArea;


function IdeaGenerator() {

    const Step = Steps.Step;

    const [currentStep, setCurrentStep] = useState(1);
    const [resume,setResume]=useState("");
    const [domain, setDomain] = useState("");
    const [domainOptions,setDomainOptions]=useState([]);
    const [passion, setPassion] = useState("");

    const [ideaOptions,setIdeaOptions]=useState([])
    const [idea,setIdea]=useState("")

    const [analyse,setAnalyse]=useState("")

    useEffect(()=>{
        if(currentStep===1){
            getIdeaDomain().then((res)=>{
                setDomainOptions(res?.data)}
            );
        }
        if(currentStep===4){
            const params={
                resume:resume,
                domain:domain,
                passion:passion
            }
            postIdeaGeneratorStep1(params).then((res)=>{
                setIdeaOptions(res?.data)}
            )
        }
        else if(currentStep===5){
            const params={
                resume:resume,
                domain:domain,
                passion:passion,
                idea:idea
            }
            postIdeaGeneratorStep2(params).then((res)=>{
                console.log('res',res)
                setAnalyse(res?.data)})
        }
    },[currentStep])

    return (
            <Card bordered={false} className="content-card">
   
            </Card>
    );
}

export default IdeaGenerator;
